  let flag = 2;

  rot += flag;
  if (rot >= 360) rot=0;
}

function drawSpiral(x, y, radiusMax, angleFactor) {
  push();
  translate(x, y);
  rotate(rot);
  scale(1.2);

  for (
    let angle = 0, radius = 0;
    radius < radiusMax;
    angle += noise(radius) * angleFactor
  ) {
    circle(sin(angle) * radius, cos(angle) * radius, 6);
    radius +=
      noise(sin(angle) * radius * noiseAmp, cos(angle) * radius * noiseAmp) *
      0.1;
    if (angle > 360) angle = 0;
  }

  pop();
}

