let colFactor = 0;

function setup() {
  createCanvas(600, 600);
  noStroke();
  ellipseMode(CENTER);
  colorMode(HSB, 360, 100, 100, 100);
  frameRate(40);

  // lines 36-43 adapted from lines 9-16 in https://compform.net/js_lab/js_lab.html?/strategy/sketches/grid_stored.js
  for (let y = 0; y < 30; y++) {
    for (let x = 0; x < 30; x++) {
      points.push({
        x: (random(width) + random(width) + random(width)) / 3,
        y: (random(height) + random(height) + random(height)) / 3,
      });
    }
  }
}

function draw() {
  background(60, 0, 20);

  // lines 50-51 adapted from lines 24-25 in https://compform.net/js_lab/js_lab.html?/strategy/sketches/grid_stored.js
  for (let i = 0; i < points.length; i++) {
    const p = points[i];

    let dMin = params.min_size;
    let dMax = params.max_size;
    let dSize = map(i, 0, points.length, dMin, dMax);

    p.x += params.noise_factor * sin(i * noise(p.x));
    p.y += params.noise_factor * sin(i * noise(p.y));

    let colH = map(dSize, dMin, dMax, params.color_small, params.color_large);
    // let alpha = (noise(i,100)*100 + (75 * cos(TWO_PI * millis() * params.speed)) + i)%100 * params.transparency;

    colFactor -= params.speed;
    if (colFactor < 0) { colFactor = 100; }
    let alpha = ((noise(i, 100) * 100 + colFactor + i) % 100) * params.transparency;
    fill(colH, 100, 100, alpha);

    // line 69 adapted from line 26 in https://compform.net/js_lab/js_lab.html?/strategy/sketches/grid_stored.js
    ellipse(p.x, p.y, dSize);
  }

}

