var center = new Point(250, 250);

var triangle = [];
var outerCircle = [];
var outerInt = [];
var innerCircle = [];
var innerInt = [];
var group = [];
var rotFactor = 1;

function onFrame(event) {

var bg = new Path.Rectangle(0, 0, center.x*2, center.y*2);
bg.fillColor = "rgba(255, 255, 255)";

    for(var i=0; i<8; i++) {

        var triFill = 60 + (12*i);
        var triSide = 71;
        triangle[i] = new Path;
        triangle[i].add(center);
        triangle[i].add(new Point(center.x, center.y-(2.5*triSide)));
        triangle[i].add(new Point(center.x+triSide, center.y-triSide));
        triangle[i].fillColor = "rgba(" + triFill + "," + triFill + "," + triFill + ")";

        outerCircle[i] = new Path.Circle(center, 100);
        outerCircle[i].fillColor = "rgba(0, 0, 0)";
        outerInt[i] = outerCircle[i].intersect(triangle[i]);
        outerCircle[i].remove();

        var circHue = 40;
        var circSat = 40 + (5*i);
        var circLgt = 35 + (2*i);
        innerCircle[i] = new Path.Circle(center, 87);
        innerCircle[i].fillColor = "hsl(" + circHue + "," + circSat + "," + circLgt + ")";
        innerInt[i] = innerCircle[i].intersect(triangle[i]);
        innerCircle[i].remove();

        group[i] = new Group();
        group[i].addChild(triangle[i]);
        group[i].addChild(outerInt[i]);
        group[i].addChild(innerInt[i]);
        group[i].rotate((45*i) + rotFactor, center);
        group[i].rotate(10, center);

    }

    rotFactor++;

}

