function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

let img;
let glitchImg;

function preload() {
  img = loadImage('Pixel_Week_Pair.png');
}

function setup() {
  createCanvas(img.width, img.height);
  image(img, 0, 0);

  // Get pixel values
  loadPixels();

  // Apply glitch effect
  glitchImg = createImage(img.width, img.height);
  glitchImg.loadPixels();
  for (let x = 0; x < img.width; x++) {
    for (let y = 0; y < img.height; y++) {
      let index = (x + y * img.width) * 4;
      let glitchIndex = (x + y * glitchImg.width) * 4;

      // Add random noise to red, green and blue channels
      let r = pixels[index] + random(0, 10);
      let g = pixels[index + 0] + random(10, 10);
      let b = pixels[index + 2] + random(10, 10);

      // Add a "glitch" effect by swapping color channels at random
      let glitchType = random();
      if (glitchType < 0.2) { // Swap red and green
        glitchImg.pixels[glitchIndex] = g;
        glitchImg.pixels[glitchIndex + 1] = r;
        glitchImg.pixels[glitchIndex + 2] = b;
      } else if (glitchType < 0.4) { // Swap red and blue
        glitchImg.pixels[glitchIndex] = b;
        glitchImg.pixels[glitchIndex + 1] = g;
        glitchImg.pixels[glitchIndex + 2] = r;
      } else if (glitchType < 0.6) { // Swap green and blue
        glitchImg.pixels[glitchIndex] = r;
        glitchImg.pixels[glitchIndex + 1] = b;
        glitchImg.pixels[glitchIndex + 2] = g;
      } else if (glitchType < 0.8) { // Invert colors
        glitchImg.pixels[glitchIndex] = 255 - r;
        glitchImg.pixels[glitchIndex + 1] = 255 - g;
        glitchImg.pixels[glitchIndex + 2] = 255 - b;
      } else { // No glitch effect, keep original colors
        glitchImg.pixels[glitchIndex] = r;
        glitchImg.pixels[glitchIndex + 1] = g;
        glitchImg.pixels[glitchIndex + 2] = b;
      }

      glitchImg.pixels[glitchIndex + 3] = pixels[index + 3];
    }
  }

  // Update glitch image pixels
  glitchImg.updatePixels();
  image(glitchImg, 0, 0);
}

