// I opted to use a class just because I find them more convenient to work with
class rothkoGenerator {
		constructor() {
//			this.rectCount = math.floor(math.random()+2);
//this is where the coinflip is made to choose between a high or low contrast composition
			this.paletteContrast = round(random());
			this.colorList = [];
//i then track the color values in the constructor			
          this.bkgColor = {
				h: 0,
				s: 0,
				l: 0
			}
			this.oneColor = {
				h: 0,
				s: 0,
				l: 0
			}
			this.twoColor = {
				h: 0,
				s: 0,
				l: 0
			}
// I also want to implement the possibility for compositions with 3 rectangles but not quite there!          
//			this.threeColor = {
//				h: 0,
//				s: 0,
//				l: 0
//			}
            this.oneBox = {
              xMiddle: 0,
              yMiddle: 0
            }
            this.twoBox = {
              xMiddle: 0,
              yMiddle: 0
            }          
			this.margin = 50;
		}
// this could definitely be optimized but the basic idea is that I generate a random background color
// I cap the saturation at 80 and light at 90 so the program doesn't get super neon colors which isn't like Rothko
		generateColors() {
			this.bkgColor.h = floor(random()*360);
			this.bkgColor.s = floor(random()*80);
			this.bkgColor.l = floor(random()*90);
// then, dependent on the contrast value I produce the colors for the two rectangles based off of the background color
//a high-contrast composition uses end bias for the color whereas a low-contrast composition uses middle bias
          if (this.paletteContrast === 1) {
			this.oneColor.h = floor(this.endBias()*90+this.bkgColor.h);
			this.oneColor.s = floor(this.endBias()*10+this.bkgColor.s+5);
			this.oneColor.l = floor(this.endBias()*10+this.bkgColor.l+5);

			this.twoColor.h = floor(this.endBias()*90+this.bkgColor.h);
			this.twoColor.s = floor(this.endBias()*10+this.bkgColor.s+5);
			this.twoColor.l = floor(this.endBias()*10+this.bkgColor.l+5);
			} else {
			this.oneColor.h = floor(this.middleBias()*20+this.bkgColor.h);
			this.oneColor.s = floor(this.middleBias()*10+this.bkgColor.s+5);
			this.oneColor.l = floor(this.middleBias()*10+this.bkgColor.l+5);

			this.twoColor.h = floor(this.middleBias()*20+this.bkgColor.h);
			this.twoColor.s = floor(this.middleBias()*10+this.bkgColor.s+5);
			this.twoColor.l = floor(this.middleBias()*10+this.bkgColor.l+5);
			}
		}

		endBias() {
			let value = random() + random();  
			value = value - 1;
			if (value < 0) value += 2;
			return value;
		}

		middleBias() {
			let value = (random() + random() + random()) / 2;
			return value;
        }
 // this is just a way to randomize the position of the line that bounds the two rectangles       
        divideCanvas() {
          this.oneBox.yMiddle = floor(windowHeight / 2) + (this.endBias()*100);
          this.twoBox.yMiddle = windowHeight-(floor(windowHeight / 2) - this.oneBox.yMiddle);
        }
 //  here is where the rectangles are actually drawn and filled in
  // I used quad() so I could be more granular with each point of the rectangle
        draw() {
          background(this.bkgColor.h, this.bkgColor.s, this.bkgColor.l);
          noStroke()
          fill(this.oneColor.h, this.oneColor.s, this.oneColor.l);
          quad(this.margin, this.margin, 
               windowWidth - this.margin, this.margin, 
               windowWidth - this.margin, this.oneBox.yMiddle, 
               this.margin, this.oneBox.yMiddle
              );

          fill(this.twoColor.h, this.twoColor.s, this.twoColor.l);
          quad(this.margin, this.oneBox.yMiddle + this.margin, 
               windowWidth - this.margin, this.oneBox.yMiddle + this.margin, 
               windowWidth - this.margin, windowHeight - this.margin, 
               this.margin, windowHeight - this.margin
              );
        }

	}

function setup() {
	createCanvas(windowWidth, windowHeight);
	colorMode(HSL);
    frameRate(0.5);
}

function draw() {
    let rothko = new rothkoGenerator();
    rothko.generateColors();
    rothko.divideCanvas();
    rothko.draw();
}

