      let size = map(lightness(col), 0, 100, 3, 1);
      console.log(size);

      let offset = width/img.width;
      let colRed = map(lightness(col), 0, 100, 0, 255);
      let flowerCol = color(colRed, 0, 255 * noise(x,y));

      let tX = x * offset;
      let tY = y * offset;

      push();

      translate(tX, tY);
      scale(size);
      rotate(random(360));
      noStroke();

      let xPos = 0;
      let yPos = 0;

      fill(flowerCol);
      ellipse(xPos-(offset/3),yPos-(offset/3), offset/1.5)
      ellipse(xPos+(offset/3),yPos-(offset/3), offset/1.5)
      ellipse(xPos-(offset/3),yPos+(offset/3), offset/1.5)
      ellipse(xPos+(offset/3),yPos+(offset/3), offset/1.5)

      fill(0);
      ellipse(xPos, yPos, offset/3);

