function ripple(c, r, a, R, G, B){
   // const steps = 200;
    const centerX = width / 2;
    const centerY = height / 2;
    // const radius = 300;

  stroke(R, G, B);
   for (let step = 1; step <= c; step++) {
    const a1 = map(step - 1, 0, c, 0, a*PI);
    let a2 = map(step, 0, c, 0,  a*PI);
    if (a2 >= a * PI) a2 -= a * PI;

    let x1 = centerX + sin(a1) * r ;
    let x2 = centerX + sin(a2) * r;
    let y1 = centerY + cos(a1) * r;
    let y2 = centerY + cos(a2) * r;

      // 
      x1 += (noise(a1, mouseX, frameCount * 0.01) - 0.5) * 150;
       x2 += (noise(a2, mouseX, frameCount * 0.01) - 0.5) * 150;
       y1 += (noise(a1, mouseY, frameCount * 0.01) - 0.5) * 150;
       y2 += (noise(a2, mouseY, frameCount * 0.01) - 0.5) * 150;

     line(x1, y1, x2, y2)
   }
}

