function generateMessage() {

  var emotions = [
    "content",
    "happy",
    "thoughtful",
    "provoked",
    "sad",
    "scared",
    "angry",
    "tired",
    "suprised",
    "out of it",
  ];

  var thought = [
    "getting some homework done",
    "grabbing lunch",
    "check my email soon",
    "getting back home soon",
    "checking out the new book store",
    "going home",
    "taking a walk in the park",
  ];

  var action = [
    "go home",
    "grab a snack",
    "go for a run",
    "take a break",
    "play some games",
    "read a book",
    "take a nap",
  ];

  var book = [
    "a novel I found in a bookstore",
    "a magazine",
    "a textbook that I need to read for school tomorrow",
    "an e-book on my Kindle",
    "an article for my research",
    "a paper that I have to grade",
    "a comic book",
  ];

  var creature = [
    "a human",
    "a cat",
    "a dog",
    "a fish",
    "an AI",
    "a message that was pre-recorded",
    "your conscious",
    "your sub-conscious",
    "your computer",
    "your phone",
  ];

  var emotionalstate = sample(emotions);
  var thoughts = sample(thought);
  var actions = sample(action);
  var books = sample(book);
  var being = sample(creature);

  var message = "";
  if(Math.random(0, 1) < 0.15 ) {
    console.log('Hello, how are you today?');
    message = `Hello, right now I'm ${emotionalstate}.`;
  } else if(Math.random(0, 1) < 0.3) {
    console.log('What are you thinking about right now?');
    message = `I'm thinking about ${thoughts}.`;
  } else if(Math.random(0, 1) < 0.45) {
    console.log('What do you want to do?');
    message = `I want to ${actions}.`;
  } else if(Math.random(0, 1) < 0.6) {
    console.log('What are you reading right now?');
    message = `I'm reading ${books}.`;
  } else if(Math.random(0, 1) < 0.75) {
    console.log('What are you?');
    message = `I'm ${being}.`;
  }

  return message;
}

function sample(array) {
  const index = Math.floor(Math.random() * array.length);
  return array[index];
}

