function generateBeat() {
  const b = [];

  const kickPattern = [1, 0, 0, 0, 1, 0, 0, 0];
  const snarePattern = [0, 0, 1, 0, 0, 0, 1, 0];
  const hatPattern = [1, 1, 1, 1, 1, 1, 1, 1];

  for (let i = 0; i < 8; i++) {
    const time = Tone.Time("4n") * i;
    if (kickPattern[i]) {
      b.push(["kick", time]);
    }
    if (snarePattern[i]) {
      b.push(["snare", time]);
    }
    if (hatPattern[i]) {
      b.push(["hatClosed", time]);
    }
  }

  return b;
}

function play(beat) {
  let t = Tone.now();
  for (const note of beat) {
    const sound = note[0];
    const time = note[1];
    console.log(sound, time);
    sampler.triggerAttackRelease(sound, Tone.Time("16n"), t + time);
  }
}

