// Set up the canvas and color palette
var canvasWidth = 800;
var canvasHeight = 800;
var colors = ['#F94144', '#F3722C', '#F8961E', '#F9C74F', '#90BE6D', '#43AA8B', '#577590'];

// Create a group to hold the pattern
var pattern = new Group();

// Create a function to generate a random number within a range
function randomInRange(min, max) {
  return Math.random() * (max - min) + min;
}

// Create a loop to draw the pattern
for (var i = 0; i < 50; i++) {
  // Create a random color for the path
  var color = colors[Math.floor(Math.random() * colors.length)];

  // Create a new path and set its properties
  var path = new Path();
  path.strokeColor = color;
  path.strokeWidth = 3;
  path.opacity = 0.5;
  path.smooth();

  // Generate a random number of points for the path
  var numPoints = Math.floor(randomInRange(3, 8));
  for (var j = 0; j < numPoints; j++) {
    // Generate a random point within the canvas bounds
    var x = randomInRange(0, canvasWidth);
    var y = randomInRange(0, canvasHeight);
    var point = new Point(x, y);

    // Add the point to the path
    path.add(point);

    // Add a small circle at the point
    var circle = new Path.Circle({
      center: point,
      radius: 5,
      fillColor: color
    });

    // Rotate the circle by a random amount
    circle.rotate(randomInRange(0, 360));

    // Add the circle to the pattern group
    pattern.addChild(circle);
  }

  // Close the path to create a shape
  path.closed = true;

  // Add the path to the pattern group
  pattern.addChild(path);
}

// Move the pattern to the center of the canvas
pattern.position = view.center;

