function drawGraph() {
  let xoff = 0;

  beginShape();
  for (let x = 0; x < width; x += 2) {
    let y = noise(xoff) * height;

    //draw line
    stroke(93, 31, 163);
    noFill();
    vertex(x, y);

    //fill
    push();
    strokeWeight(2);
    line(x, y, x, 400);
    pop();

    xoff += 0.02;
  }
  endShape();
}

function drawCircles(r, g1, g2, b1, b2, alpha, density, min, max, blend) {
  let yoff = 0;

  for (let x = 0; x < width; x += density) {
    push();
    blendMode(blend);

    let y = noise(yoff) * 1000 - 300;
    let colourR = r;
    let colourG = map(y, width, 0, g1, g2);
    let colourB = map(y, width, 0, b1, b2);

    noStroke();
    fill(colourR, colourG, colourB, alpha);

    circle(x, y, round(random(min, max)));

    pop();

    yoff += 10;
  }
}

