let frequency_slider;
let amplitude_slider;
let timespeed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 25);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  timespeed_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let timeSpeed = frameCount / 10;
  let frequency = frequency_slider.value();

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(i * frequency, 0) - 0.5) * amplitude * timeSpeed;
    let offsetY = (noise(i * timeSpeed, 1000) - 0.5) * amplitude * timeSpeed;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

