class Key {
  constructor(x, y, w, k, code) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.k = k;
    this.code = code;
  }

  render() {
    stroke(0);
    if (keyIsDown(this.code)) {
      fill(0);
    } else {
      noFill();
    }
    rect(this.x + 5, this.y + 5, this.w-10, 40);
   textAlign(CENTER, CENTER);
    noStroke();
    if (keyIsDown(this.code)) {
      fill(255);
    } else {
      fill(0);
    }
      textSize(14);
  noStroke();
    text(this.k, this.x + this.w/2, this.y + 25);
  }
}

