let n;
let fc;

function setup() {
  createCanvas(600, 600, WEBGL);
  //debugMode();
  frameRate(30);
}

function draw() {
  background(20, 61, 60);

  for(i = 0; i < width; i += 20) {
    n = noise(i * frameCount);
    fc = frameCount / 3 * 0.03;
    pointLight(150, 200, 200, 0, 100, 0);
    ambientMaterial(50);
    noStroke();

    //box translation experiment

    translate(frameCount + n / 5, 0, 0);
    rotateX(fc);
    box(100 + n);

    translate(frameCount + n / 5, 0, 0);
    rotateY(fc);
    box(100 + n);

    translate(frameCount + n / 5, 0, 0);
    rotateZ(fc);
    box(100 + n);

    //box translation reversed

    push();

    translate(-(frameCount + n / 5), 0, 0);
    rotateX(-fc);
    sphere(60 - n);

    translate(-(frameCount + n / 5), 0, 0);
    rotateY(-fc);
    sphere(60 - n);

    translate(-(frameCount + n / 5), 0, 0);
    rotateZ(-fc);
    sphere(60 - n);

    pop();
  }

  camera(-500, 100, -400);
  //orbitControl();
}

