function sortImage() {
  loadPixels();

  for (let y = 0; y < height; y++) {
    if (random() > 0.1) { // randomly select rows to sort
      let row = [];
      let start = floor(random(width)); // random starting point for sorting
      let end = start; // end point for sorting
      let maxSum = 0; // highest pixel value sum found so far
      for (let x = start; x < width; x++) {
        let index = (x + y * width) * 4;
        let r = pixels[index];
        let g = pixels[index + 1];
        let b = pixels[index + 2];
        let sum = r + g + b;
        row.push([r, g, b]);
        if (sum > maxSum) { // update end point and maxSum if a higher sum is found
          end = x;
          maxSum = sum;
        }
      }

      row = row.slice(0, end - start + 1); // only sort up to the highest value pixel
      row.sort(function(a, b) {
        let aSum = a[0] + a[1] + a[2];
        let bSum = b[0] + b[1] + b[2];
        return aSum - bSum;
      });

      for (let x = start; x <= end; x++) {
        let index = (x + y * width) * 4;
        pixels[index] = row[x - start][0];
        pixels[index + 1] = row[x - start][1];
        pixels[index + 2] = row[x - start][2];
      }
    }
  }

  updatePixels();
}

