function drawSpiral(x, y) {
  push();

  for (
    let angle = 0, radius = 0;
    radius < params.Width;
    angle += params.AngleFactor
  ) {

    let xPos = x + sin(angle) * radius;
    let yPos = y + cos(angle) * radius;

    circle(
      xPos + noise(xPos*params.Smoothing, yPos*params.Smoothing, 1) * params.NoiseAmp,
      yPos + noise(yPos*params.Smoothing, xPos*params.Smoothing, 0) * params.NoiseAmp,
      params.StrokeWidth
    );

    radius += params.RadiusFactor;

    if (angle > 360) angle = 0;
  }

  pop();
}

