const pane = new Tweakpane.Pane();


let circles = [];
const numCircles = 8;
const radius = 40;

let inc = 0.04;

// const params = {
//   Easing: 0.01,
//   NoiseFactor: 40,
//   AngleIncrease: 0.07
// }

const params = {
  Easing: 0.02,
  NoiseFactor: 5,
  AngleIncrease: 0.02
}

pane.addInput(params, "Easing", { min: 0, max: 0.2 });
pane.addInput(params, "NoiseFactor", { min: 0, max: 50 });
pane.addInput(params, "AngleIncrease", { min: 0.01, max: 0.1 });


let theta = 0;

let colours = [];

function setup() {
  createCanvas(windowWidth, windowHeight);
  noStroke();
  createCircle ();
  setColours(numCircles);
  frameRate(60);

  rectMode(CENTER);
  fill("rgb(90,90,90)");
  textSize(24);
  text("Click to change colour", width/2 -120, height/2);
}

function createCircle () {
  for (let i = 0; i < numCircles; i++) {
    const x = mouseX;
    const y = mouseY;
    circles.push({ x, y });
  }
}

function setColours(num) {

  let startR = floor(random(255));
  let startG = floor(random(255));
  let startB = floor(random(255)); 
  let startColor = color(startR, startG, startB);

  let endR = floor(random(255));
  let endG = floor(random(255));
  let endB = floor(random(255));
  let endColor = color(endR, endG, endB);

  for (let i = 0; i<num; i++) {
    colours[i] = lerpColor (startColor, endColor, i/num);
  }
}

function draw() {

  for (let i = 0; i < numCircles; i++) {
    let circle = circles[i];
    const dx = mouseX - circle.x;
    const dy = mouseY - circle.y;
    circle.x += dx * params.Easing;
    circle.y += dy * params.Easing;

    let r = 50;
    let a = r * cos(theta);
    let b = r * sin(theta);
    theta += params.AngleIncrease; if (theta > TWO_PI ) theta = 0;

    let ex = noise(i * dx * 5) * params.NoiseFactor;
    let ey = noise(i * dy * 5) * params.NoiseFactor;
    // let ex=0, ey=0;

    fill(colours[i]);
    ellipse(circle.x + ex + a, circle.y + ey + b, radius);
  }
}

function mouseClicked() {
  setColours(circles.length);
}

