function setup() {
  createCanvas(windowWidth, windowHeight);

  px = width / 2;
  py = height / 2;

  dir = 1;
}

function draw() {
  background(0, 0, 50, 80);

  for(let i=0; i<width; i+=20){
    // noStroke()
    stroke(255, 255, 255, 100);
    noFill();
    // fill(255, 255, 255, 10);
    // y = noise(i*0.1)*100-50;
    y = noise(frameCount+i*.001)*1000-50;    
    y = noise(frameCount*.01+i*.001)*1000-50;
    y = noise(frameCount*.001+i*.001)*1000-50;
    ellipse((width/10+y), i, 100+y, 100+y)
    ellipse(width/6+y, i, 80-y, 80-y)
  }

  if (dist(mouseX, mouseY, px, py) > 15) {
    push();
    translate(mouseX, mouseY);
    if (mouseX < px) {
      rotate(PI + atan((mouseY - py) / (mouseX - px)));
    } else {
      rotate(atan((mouseY - py) / (mouseX - px)));
    }
    scale(1, dir);
    rotate(PI / 12);
    push();
    rotate(PI / 2);
    // stroke(255);
    // fill(0, 0, 255);
    push();
    scale(3);
    fill(255, 200);
    triangle(0, 0, -12, 22, 12, 22);

    fill(255, 75)

    triangle(0, 30, 0, 40, 20, 50);
    triangle(0, 30, 0, 45, 20, 50);
    // triangle(0, 30, 20, 45, 20, 50);
    triangle(0, 30, 0, 40, -20, 50);
    triangle(0, 30, 0, 45, -20, 50);
    triangle(0, 30, 0, 45, -20, 50);

    fill(255, 64.7, 0);
    ellipse(0, 20, 15, 40);
    stroke(0)
    fill(255, 255, 255);
    ellipse(0, 20, 15, 30);
    fill(255, 64.7, 0);
    ellipse(0, 20, 15, 20);
    // fill(255, 255, 255);
    // ellipse(0, 20, 15, 5);
    push()
    stroke(255);
    strokeWeight(1);
    fill(0);
    ellipse(5, 5, 5, 5);
    ellipse(-5, 5, 5, 5);
    pop()
    pop();
    pop();
    pop();
    px = mouseX;
    py = mouseY;
  }
}

let rows = 10;
let cols = 10;
let tileSize = 40;
let noiseScale = 0.02;
let uniqueX, uniqueY;

function setup() {
  createCanvas(500, 500);
  noStroke();
  // Set a noise seed for consistency
  noiseSeed(12345);
  uniqueX = int(random(cols));
  uniqueY = int(random(rows));
}

function draw() {
  background(220);

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let x = i * tileSize;
      let y = j * tileSize;

      if (i === uniqueX && j === uniqueY) {
        // Unique color for a specific square
        fill(255, 0, 0);
      } else {
        let noiseVal = noise(i * noiseScale, j * noiseScale, frameCount * 0.01);
        let c = map(noiseVal, 0, 1, 0, 255);
        fill(c);
      }

      rect(x, y, tileSize, tileSize);
    }
  }
}

