const canvas = document.getElementById('canvas');
const ctx = canvas.getContext('2d');
const svg = document.getElementById('svg');
const textPath = document.createElementNS('http://www.w3.org/2000/svg', 'path');
const textContentX = 'COMP FORM! COMP FORM! COMP FORM! COMP FORM! COMP FORM! COMP FORM! COMP FORM! COMP FORM! COMP FORM!';

// let amplitude = 50;
// let frequency = 0.5;
// let fontSize = 12;
let amplitude = 25;
let frequency = .03;
let fontSize = 12;
const xOffset = 0;
const yOffset = canvas.height / 2;
function setup() {
  background(0);
}
function draw() {



  const font = 'Helvetica-light';
  // Get number of copies from slider
  const numCopies = parseInt(document.getElementById('copies').value);
  const spaceBetweenCopies = parseInt(document.getElementById('space').value);

  // Remove existing text and text path elements
  for (let i = 0; i < numCopies; i++) {
    const textPath = document.getElementById(`text-path-${i}`);
    const textEl = document.getElementById(`text-${i}`);
    if (textPath) {
      textPath.remove();
    }
    if (textEl) {
      textEl.remove();
    }
  }

  // Clear canvas
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  // Draw sine wave path for each copy
  for (let i = 0; i < numCopies; i++) {
    // const yOffset = i * (canvas.height / numCopies);
    const yOffset = i * (canvas.height / numCopies) + i * spaceBetweenCopies;
    ctx.beginPath();
    for (let x = 0; x < canvas.width; x++) {
      const y = amplitude * Math.sin(x * frequency + xOffset) + yOffset;
      ctx.lineTo(x, y);
    }
    ctx.stroke();

    // Set text path to sine wave path for each copy
    const textPath = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    textPath.setAttribute('d', `M 0 ${yOffset}`);
    for (let x = 0; x < canvas.width; x++) {
      const y = amplitude * Math.sin(x * frequency + xOffset + millis()/1000) + yOffset;
      // const y = amplitude * Math.sin(x * frequency + xOffset) + yOffset;
      textPath.setAttribute('d', `${textPath.getAttribute('d')} L ${x} ${y}`);
    }
    textPath.setAttribute('id', `text-path-${i}`);
    textPath.setAttribute('fill', 'none');
    svg.appendChild(textPath);

    // Create new text element and set its text path to the sine wave path for each copy
    const textEl = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    textEl.setAttribute('id', `text-${i}`);
    textEl.setAttribute('font-size', fontSize);
    textEl.setAttribute('fill', 'white');


  textEl.setAttribute('style', `font-family: ${font};`);
    const textPathEl = document.createElementNS('http://www.w3.org/2000/svg', 'textPath');
    textPathEl.setAttribute('href', `#text-path-${i}`);
    // textPathEl.setAttribute('startOffset', '50%');
    textPathEl.setAttribute('startOffset', '0%');
    textPathEl.textContent = textContentX;
    textEl.appendChild(textPathEl);
    svg.appendChild(textEl);


  }
}




function updateAmplitude(event) {
  amplitude = event.target.value;
  svg.innerHTML = '';
  draw();
}

function updateFrequency(event) {
  frequency = event.target.value;
  svg.innerHTML = '';
  draw();
}

function updateFontSize(event) {
  fontSize = event.target.value;
  const textEl = document.getElementById('text');
  if (textEl) {
    textEl.setAttribute('font-size', fontSize);
  }
}

document.getElementById('amplitude-slider').addEventListener('input', updateAmplitude);
document.getElementById('frequency-slider').addEventListener('input', updateFrequency);
document.getElementById('font-size-slider').addEventListener('input', updateFontSize);

