let myTurtle;

function setup() {
  createCanvas(500, 500);
  strokeWeight(2);
  stroke(0);
  background("#5D5D5D");
  noLoop();
  myTurtle = new Turtle();
}

function draw() {
  myTurtle.penUp();
  myTurtle.moveTo(250, 450);
  myTurtle.turnTo(-70);
  myTurtle.penDown();
  drawBranch(100);
}

function drawBranch(length) {
  if (length < 4) {
    return;
  }

  // draw this branch
  let branchColor = color(random(50, 200), random(50, 200), random(50, 200));
  stroke(branchColor);
  myTurtle.moveForward(length * 0.5);
  myTurtle.turnLeft(10);
  myTurtle.moveForward(length * 0.5);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(20, 40));
  drawBranch(length * 0.75);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(random(10, 30));
  drawBranch(length * 0.75);
  myTurtle.popState();
}

