let myTurtle;

function setup() {
  createCanvas(900, 900);
  noFill();
  stroke(255);
  background(50);
  noLoop();

  myTurtle = new Turtle(width/2, height/2);
}

function draw() {
  const numCircles = 10;
  const circleSpacing = 30;
  const circleSize = 200;

  for (let i = 0; i < numCircles; i++) {
    myTurtle.pushState();
    myTurtle.penUp();
    myTurtle.moveForward(i * circleSpacing);
    myTurtle.penDown();
    drawCircle(circleSize - i * 20);
    myTurtle.popState();
  }
}

function drawCircle(radius) {
  const numPoints = 60;
  const angle = 360 / numPoints;

  myTurtle.moveForward(radius);
  for (let i = 0; i < numPoints; i++) {
    myTurtle.turnLeft(angle);
    myTurtle.moveForward(2 * radius * Math.PI / numPoints);
  }
}

