class Building {
  constructor(x, z, size, maxSubdivisions) {
    this.x = x;
    this.y = 0;
    this.z = z;
    this.width = size * random(0.5, 1.5);
    this.depth = size * random(0.5, 1.5);
    this.height = size * random(0.5, 1.5);
    this.color = color(random(50, 200), random(50, 200), random(50, 200));
    this.maxSubdivisions = maxSubdivisions;
    this.subdivided = false;
  }

  display() {
    fill(this.color);
    push();
    translate(this.x, this.y + this.height / 2, this.z);
    box(this.width, this.height, this.depth);
    pop();
    if (!this.subdivided && this.maxSubdivisions > 0 && random() < 0.2) {
      this.subdivided = true;
      this.maxSubdivisions--;
      let newSize = this.width/2;
      let building1 = new Building(this.x-newSize, this.z, newSize, this.maxSubdivisions);
      let building2 = new Building(this.x+newSize, this.z, newSize, this.maxSubdivisions);
      buildings.push(building1, building2);
    }
  }

  getBoundingBox() {
    return {
      minX: this.x - this.width / 2,
      minY: this.y,
      minZ: this.z - this.depth / 2,
      maxX: this.x + this.width / 2,
      maxY: this.y + this.height,
      maxZ: this.z + this.depth / 2
    }
  }

  getBottomCenter() {
    const bbox = this.getBoundingBox();
    return createVector((bbox.minX + bbox.maxX) / 2, bbox.minY, (bbox.minZ + bbox.maxZ) / 2);
  }
}

