function draw() {
  background(220);
  noStroke();

  //map value of slider
  let size = slider.value();

  //Draw the grid of cricles
  for (let x = 0; x <= width; x += size) {
    for (let y = 0; y <= height; y += size) {
      // Get noise value for this position
      let noiseVal = noise(x * 0.01, y * 0.01);
      // Set fill color based on noise value
      fill(noiseVal * 255, noiseVal * 255, 300); 
      // circle with diameter based on noise value
      circle(x, y, size * noiseVal);
    }
  }
}

