// initialize variables
let playerX = 50;
let playerY = 50;
let playerSize = 20;
let playerSpeed = 5;
let coinX;
let coinY;
let coinSize = 15;
let score = 0;
let obstacleX = 300;
let obstacleY = 100;
let obstacleWidth = 50;
let obstacleHeight = 80;

function setup() {
  createCanvas(500, 500);
  // set the initial position of the coin randomly
  coinX = random(width - coinSize);
  coinY = random(height - coinSize);
}

function draw() {
  background(200);

  // move the player based on arrow key inputs
  if (keyIsDown(LEFT_ARROW)) {
    playerX -= playerSpeed;
  }
  if (keyIsDown(RIGHT_ARROW)) {
    playerX += playerSpeed;
  }
  if (keyIsDown(UP_ARROW)) {
    playerY -= playerSpeed;
  }
  if (keyIsDown(DOWN_ARROW)) {
    playerY += playerSpeed;
  }

  // check if the player has collided with the coin
  if (dist(playerX, playerY, coinX, coinY) < playerSize/2 + coinSize/2) {
    // increase the score and reset the coin's position
    score++;
    coinX = random(width - coinSize);
    coinY = random(height - coinSize);
  }

  // check if the player has collided with the obstacle
  if (playerX + playerSize > obstacleX &&
      playerX < obstacleX + obstacleWidth &&
      playerY + playerSize > obstacleY &&
      playerY < obstacleY + obstacleHeight) {
    // reset the player's position and score
    playerX = 50;
    playerY = 50;
    score = 0;
  }

  // draw the player, coin, and obstacle
  fill(255, 0, 0);
  rect(obstacleX, obstacleY, obstacleWidth, obstacleHeight);
  fill(255, 255, 0);
  ellipse(coinX, coinY, coinSize);
  fill(0, 0, 255);
  rect(playerX, playerY, playerSize, playerSize);

  // display the score
  textSize(24);
  fill(0);
  text(`Score: ${score}`, 20, 30);
}

