function endGame() {
  for (let i = 0; i < allBrogli.length; i++) {
    allBrogli[i].setVelocity(0,0);
    allBrogli[i].position.y = Math.floor(Math.random() * 600 + 100)
    allBrogli[i].position.x = Math.floor(Math.random() * 600 + 100)
  }
}

function resetGame() {
  speedIncrease += .5
  for (let i = 0; i < allBrogli.length; i++) {
    allBrogli[i].setVelocity(0,0);
    allBrogli[i].position.y = -100

  }
  initGame()
}

function initGame() {
  console.log('game init')

  let safePoint = Math.floor(Math.random() * 400 + 200);
  let lowerDiv = (safePoint - 200) / (allBrogli.length / 2)
  let higherDiv = (700 - (safePoint + 200)) / (allBrogli.length / 2)
  for (let i = 0; i < allBrogli.length; i++) {
    allBrogli[i].position.y = Math.random() * 80 + 10;

    if ( i < allBrogli.length / 2) {
      let distX = 100 + (lowerDiv*i)
      allBrogli[i].position.x = distX; 
    } else {
      let distX = (safePoint + 100) + (higherDiv * i)
      allBrogli[i].position.x = distX; 
    }
    allBrogli[i].setVelocity(0, 4 + speedIncrease)
  }
}

