let c;
let r;
let g;
let b;

function setup() {
  createCanvas(400, 400);

  createP("Red");
  r_slider = createSlider(0, 255, 50);

  createP("Green");
  g_slider = createSlider(0, 255, 50);

  createP("Blue");
  b_slider = createSlider(0, 255, 50);
}

function draw() {
  background(10);

  img = createImg(600, 600);
  loadPixels();

  r = r_slider.value();
  g = g_slider.value();
  b = b_slider.value();

  for(y = 0; y < height; y = y + 10) {
    for(x = 0; x < width; x = x + 10) {
        c = color(y * (r/50), x * (g/50), y * b/50);
        set(x, y, c);
    }
  }

  updatePixels();
  image(img, 0, 0, width, height);
}

function setup() {
  createCanvas(600, 800);
  world.gravity.y = 10;

  // Establish sprites

  player1 = new Sprite();
  player1.x = 200;
  player1.y = 600;
  player1.collision = 'kinematic';

  player2 = new Sprite();
  player2.x = 400;
  player2.y = 600;
  player2.collision = 'kinematic';

  obstacle = new Sprite();
  obstacle.y = 100;
  obstacle.width = random(100, 400);
  obstacle.height = random(100, 400);

  floor = new Sprite(400, 700, 800, 30, 'static');
}

function draw() {
  background(220);

  // Establish movement

  // Player 1 - use s, f, e

  if(kb.pressing('s')) {
    player1.vel.x = -3;
  } else if(kb.pressing('f')) {
    player1.vel.x = 3;
  } else if(kb.pressing('e')) {
    player1.vel.y = -5;
  } else {
    player1.vel.x = 0;
  }

  // Player 2 - uses arrow keys

  if(kb.pressing('left')) {
    player2.vel.x = -3;
  } else if(kb.pressing('right')) {
    player2.vel.x = 3;
  } else if(kb.pressing('up')) {
    player2.vel.y = -5;
  } else {
    player2.vel.x = 0;
  }

  // Establish lose condition

  if(obstacle.collides(floor)) {
    floor.collider = 'dynamic';
  }
}

