let video;
let poseNet;
let pose;
let prevNosePos;
let noseVelocity;

let col;

function setup() {
  createCanvas(640, 480);
  noStroke();

  video = createCapture(VIDEO);
  video.size(width, height);

  poseNet = ml5.poseNet(video, modelReady);
  poseNet.on('pose', gotPose);
}

function modelReady() {
  console.log('PoseNet model loaded');
}

function gotPose(results) {
  if (results.length > 0) {
    pose = results[0].pose;
  }
}

function draw() {
    push();
    tint(255, 30);
    image(video, 0, 0);
    pop();

  if (pose) {
    let nose = pose.keypoints[0];
    let nosePos = createVector(nose.position.x, nose.position.y);

    if (prevNosePos) {
      let deltaTime = 1 / frameRate();
      noseVelocity = p5.Vector.sub(nosePos, prevNosePos).div(deltaTime);
      console.log('Nose velocity:', noseVelocity.mag());

      if (noseVelocity.mag() > 200) {
          col = 255;
      } else {
          col = 0;
      }
    }

    prevNosePos = nosePos;

    fill(col, 0, 0);
    ellipse(nose.position.x, nose.position.y, 10);
  }
}

var col = 0;
var inc = 0.01;
var start = 0

function setup() {
   createCanvas(windowWidth, windowHeight);

}

function draw() {
	background(0,10);
	fill(120);
	var xoff = start;
	for (var x = 0; x < width; x++) {

		var y = noise(xoff)*height;

		ellipse(x,y,y)

		xoff += inc;
}

	start += inc;

	//endShape();
	//noLoop();

	if (col <= 255) {
		col++;
	} else if (col > 255) {
		col = 0;
}

}

// Define variables
tower_height = 50;
tower_radius = 10;
wall_length = 100;
wall_thickness = 5;
wall_height = 20;
roof_height = 30;

// Define tower module
module tower() {
  difference() {
    cylinder(h=tower_height, r=tower_radius, center=true);
    translate([0, 0, tower_height/2]) cylinder(h=5, r=tower_radius*2, center=true);
  }
}

// Draw castle walls
difference() {
  color("gray") cube([wall_length, wall_thickness, wall_height], center=true);
  color("gray") translate([0, -wall_length/2 + wall_thickness/2, 0]) cube([wall_thickness, wall_length - wall_thickness, wall_height], center=true);
  color("gray") translate([0, wall_length/2 - wall_thickness/2, 0]) cube([wall_thickness, wall_length - wall_thickness, wall_height], center=true);
  color("gray") translate([-wall_length/2 + wall_thickness/2, 0, 0]) cube([wall_length - wall_thickness*2, wall_thickness, wall_height], center=true);
  color("gray") translate([wall_length/2 - wall_thickness/2, 0, 0]) cube([wall_length - wall_thickness*2, wall_thickness, wall_height], center=true);
}

// Draw towers
translate([-wall_length/2 + tower_radius, -wall_length/2 + tower_radius, 0]) color("darkred") tower();
translate([-wall_length/2 + tower_radius, wall_length/2 - tower_radius, 0]) color("darkred") tower();
translate([wall_length/2 - tower_radius, wall_length/2 - tower_radius, 0]) color("darkred") tower();
translate([wall_length/2 - tower_radius, -wall_length/2 + tower_radius, 0]) color("darkred") tower();

// Roof
translate([0, 0, wall_height + 20]) color("darkgray") cube([wall_length, wall_length, roof_height], center=true);

// Add tower on top of roof
translate([0, 0, wall_height + roof_height + 10]) color("darkred") tower();

// Add triangle on top of top tower
translate([0, 0, 85]) color("gold") polyhedron(points=[[-10, -10, 0], [10, -10, 0], [0, 0, 20], [0, 10, 0]], faces=[[0, 1, 2], [1, 3, 2], [3, 0, 2], [0, 3, 1]]);

