//sound from https://pixabay.com/sound-effects/search/bounce/

let topwall;
let leftwall;
let rightwall;
let wallwidth = 10;

let object;
let objectwidth = 20;
let playerwall;
let playerwallwidth = 100;

let gamespeed = 5;
let score = 0;
let bouncesound;

let mode = 0;

function setup() {
  createCanvas(400, 400);

  object = createSprite(width/2, 50, objectwidth, objectwidth);
  object.setSpeed(gamespeed, random(360));

  walls = new Group();
  topwall = createSprite(width/2, wallwidth/2, width, wallwidth);
  topwall.immovable = true;
  walls.add(topwall);
  leftwall = createSprite(wallwidth/2, height/2, wallwidth, height);
  leftwall.immovable = true;
  walls.add(leftwall);
  rightwall = createSprite(width - wallwidth/2, height/2, wallwidth, height);
  rightwall.immovable = true;
  walls.add(rightwall);

  playerwall = createSprite(width/2, height - wallwidth/2 , playerwallwidth, wallwidth);
  playerwall.immovable = true;

  bouncesound = createAudio('bounce.mp3');

}

function draw() {
  background(30);

  object.bounce(walls);
  object.bounce(playerwall);

  drawSprites();

  if(keyIsDown(37)){
    playerwall.position.x-=gamespeed;
  }
  if(keyIsDown(39)){
    playerwall.position.x+=gamespeed;
  }
  if(playerwall.position.x < playerwallwidth/2){
    playerwall.position.x = playerwallwidth/2;
  }
  if(playerwall.position.x > width - playerwallwidth/2){
    playerwall.position.x = width - playerwallwidth/2;
  }

  push();
    fill(255);
    textAlign(LEFT);
    text("Score: " + score, 20, 30);
    textAlign(RIGHT);
    text("Use â† â†’ to navigate", width - 20, 30);
  pop();

  if (object.position.y >= height - wallwidth - objectwidth/2 && object.position.y < 1 + height - wallwidth - objectwidth/2) {
    console.log("bounce");
    score++;
    bouncesound.play();
  }

  if (object.position.y > height) {
    push();
      fill(255);
      textAlign(CENTER, CENTER);
      text("Oops", width/2, height/2 - 10);
      text("Press right arrow to try again", width/2, height/2 + 10);
      if (keyIsDown(39)) {
        window.location.reload();
      }
    pop();
  }
}

