  myTurtle.penUp();
  myTurtle.moveTo(50, 50);
  myTurtle.penDown();

  myTurtle.moveTo(100, 50);

  for(i = 0; i < 3; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(120);
  }

  myTurtle.moveTo(200, 50);

  for(i = 0; i < 4; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(90);
  }

  myTurtle.moveTo(300, 50);

  for(i = 0; i < 5; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(72);
  }

  // 2nd Row

  myTurtle.penUp();
  myTurtle.moveTo(50, 125);
  myTurtle.penDown();

  myTurtle.moveTo(100, 125);

  for(i = 0; i < 6; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(60);
  }

  myTurtle.moveTo(200, 125);

  for(i = 0; i < 7; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(51.4);
  }

  myTurtle.moveTo(300, 125);

  for(i = 0; i < 8; i++) {
    myTurtle.moveForward(30);
    myTurtle.turnRight(45);
  }

  // 3rd Row

  myTurtle.penUp();
  myTurtle.moveTo(50, 225);
  myTurtle.penDown();

  myTurtle.moveTo(100, 225);

  for(i = 0; i < 9; i++) {
    myTurtle.moveForward(25);
    myTurtle.turnRight(40);
  }

  myTurtle.moveTo(200, 225);

  for(i = 0; i < 10; i++) {
    myTurtle.moveForward(25);
    myTurtle.turnRight(36);
  }

  myTurtle.moveTo(315, 225);

  for(i = 0; i < 360; i++) {
    myTurtle.moveForward(.8);
    myTurtle.turnRight(1);
  }

  noLoop();
}

