const storyGrammar = {
  story: ["#person# #feeling#. So, for their comp form sketch, they #sketch#. This week, they made #number# sketches! Finally, #reason#, they #verb#, and #verb#."],

  number: ["4", "5", "10", "100", "500", "1000", "1 million"],
  person: ["Chris", "Isha", "Justin", "Rama", "Reshma", "Alex", "Aabha", "Brian", "Shristi", "Sora", "John", "Sahiti", "Ishaan", "Keren"],
  reason: ["before it was too late", "just for the hell of it", "because they wanted to go above and beyond"], 
  feeling: ["felt determined", "felt stressed", "felt inspired", "felt cRaZy"],
  adjective: ["cool", "weird", "strange", "nice", "insane", "crazy", "comical", "wild", "quirky", "funky"],
  sketch: ["did something #adjective# with circles", "made a #adjective# animation", "created #adjective# custom type", "made a #adjective# beat with tone.js", "generated #adjective# text", "made a #adjective# microgame", "tried to figure out the strategies homework but failed", "tried to figure out the strategies homework and SUCCEEDED" ],
  verb: ["read the chapter", "asked #number# questions on the notion page", "did the solo challenge", "tried something new in p5", "did the pair challenge with #person#", "printed a sketch in the laser lab"],
};

function main() {
  const grammar = tracery.createGrammar(storyGrammar);
  const story = grammar.flatten("#story#");
  display(story);
}

function display(...strings) {
  const div = document.createElement("div");
  div.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
  div.innerText = strings.join("\n");
  document.body.append(div);
}

