        {
            for (int j = 1; j <= NoOfCyls /80; j++)
            {
                Vector3 position;
                float y = Mathf.PerlinNoise(i * 0.1f, j * 0.1f) * -4.6f + 3;

                if (i%2 == 0)
                {
                    position = new(i * 0.8f, y, j * 0.8f);

                }
                else
                {
                    position = new(i * 0.8f, y, j * 0.8f - 0.4f);
                }

                Instantiate(terrainCyl, position, Quaternion.identity, transform);
            }

        }


    }


}

