// Dot Challenge Starting Point
let diameter;
function setup() {
  createCanvas(400, 400);
  colorMode(HSB);
}

function draw() {
  background(20);

  noStroke();
  ellipseMode(CENTER);

  let noiseFrequency = 0.02;

  for (let i = 0; i < 100; i++) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    let x = noise(i * 20, 0) * 400;
    let y = noise(i * 20, 1000) * 400;

    // the diameter shouldn't always be 10, it needs to vary
    if(x>120&&x<280&&y>120&&y<280){

       fill(max(random(280),random(290),random(300)),100,100);
      diameter = random(12,18)+ noise(1.05)*2;
    }
    else{
          fill(max(random(100),random(110),random(120)),100,100);
      diameter = random(4,10)+noise(1.001)*5;
    }


    // the colors also need to change
    // what colorMode would be easiest to work with?


    ellipse(x+noise(1)*10, y+noise(1.001)*10, diameter, diameter);
  }

  noLoop();
}
