var myTurtle;
var startFrame;
var underlyingImage;


function preload() {
  var img = "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ea/Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg/1200px-Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg";
  underlyingImg = loadImage(img);
}

function setup() {
    createCanvas(700, 466);
    background(0);
    underlyingImg.loadPixels();
    myTurtle = new Turtle(width/2, height/2);
    myTurtle.setColor(color(255,200,200));
    myTurtle.setWeight(6); 
    myTurtle.penDown();
    resetCanvas();
}

function draw() {
    var step = (frameCount - startFrame)/100;
    myTurtle.forward(step);
    myTurtle.left(5.0);

    var theColorAtMxMy = underlyingImg.get(floor(myTurtle.x), floor(myTurtle.y));
    myTurtle.setColor(theColorAtMxMy);
    if (myTurtle.y > height*1.5) resetCanvas();

}

function resetCanvas() {
    background(0);
    startFrame = frameCount;
    myTurtle.penUp();
    myTurtle.goto(width/2, height/2);
    myTurtle.penDown();
}

