let mySound;
let mySound2;
let fft;
let fft2;

function preload() {
    mySound = loadSound('/sound/sound1.wav');

}

function setup() {
    createCanvas(500, 500);
    fill(random(255),random(255),random(255));

    fft = new p5.FFT(.8, 1024);
    fft.setInput(mySound);
    fft2 = new p5.FFT(.8, 128);
    fft.setInput(mySound2);

    startButton = createButton('start');
    startButton.mousePressed(start);

    stopButton = createButton('stop');
    stopButton.mousePressed(stop);
}

function start() {
    mySound.loop(0, 1, 4, 0, 4);
    mySound2.loop(1, 1, 4, 0, 2);
}

function stop() {
    mySound.pause();
    mySound2.pause();
}

function draw() {
    background(255);
    //fill(255);
    noStroke();

    const data = fft.analyze();

    for (let i = 500; i < 1000; i++) {
        const x = map(i, 0, 1024, 0, 500);
        const y = map(data[i], 0, 255, 450, 10);
        ellipse(x, y, 2, 2);
    }
    const data2 = fft2.analyze();

    for (let i = 0; i < 500; i++) {
        const x = map(i, 0, 1024, 0, 500);
        const y = map(data[i], 0, 255, 200, 10);
        ellipse(x, y, 2, 2);
    }
    for (let i = 200; i < 600; i++) {
        const x = map(i, 0, 1024, 0, 500);
        const y = map(data[i], 0, 255, 300, 10);
        ellipse(x, y, 2, 2);
    }
    for (let i = 400; i < 800; i++) {
        const x = map(i, 0, 1024, 0, 500);
        const y = map(data[i], 0, 255, 375, 10);
        ellipse(x, y, 2, 2);
    }

}

