    myTurtle.popState();


    // fin
    myTurtle.pushState();
        left(50); move(30); left(70); move(10); move(24);
    myTurtle.popState();

    // R1
    myTurtle.pushState();
        up(); right(45); move(10); down();
        left(75); move(20); 
        right(20); move(40); left(80); move(60); left(30); move(40); right(30); move(20);
    myTurtle.popState();

    // R2
    myTurtle.pushState();
        up(); move(10); down(); right(75); move(10); down();
        left(75); move(20); 
        right(20); move(40); left(80); move(60); left(30); move(40); right(30); move(20);
    myTurtle.popState();

    // R3-4 position
    up(); right(55); move(30); down();

    // R3
    myTurtle.pushState();
        left(30); move(20); right(20); move(80); right(30); move(80); left(20); move(30);
    myTurtle.popState();

    // R4
    myTurtle.pushState();
        left(10); move(20); right(20); move(80); right(30); move(80); left(20); move(30);
    myTurtle.popState();

    up(); right(125); move(87); down();

    // L3
    myTurtle.pushState();
        left(25); move(20); left(20); move(80); left(30); move(80); right(20); move(30);
    myTurtle.popState();

    // L4
    myTurtle.pushState();
        left(45); move(20); left(20); move(80); left(30); move(80); right(20); move(30);
    myTurtle.popState();

    up(); right(120); move(15); down();

    // L2
    myTurtle.pushState();
        left(115); move(20); 
        left(30); move(40); right(80); move(60); right(30); move(40); left(30); move(20);
    myTurtle.popState();

    up(); right(10); move(7); down();

    // L1
    myTurtle.pushState();
        left(105); 
        move(10); 
        left(20); move(40); right(80); move(60); right(30); move(40); left(30); move(20);
    myTurtle.popState();

    // fin
    myTurtle.pushState();
        up(); move(10); down();
        left(90); move(30); right(70); move(10); move(24);
    myTurtle.popState();

    noLoop();
    // setFrameRate(1);

    up();
    myTurtle.moveTo(width/2, height/2-100);
}





