var myTurtle;

function setup() {
	createCanvas(600, 600);
	myTurtle = new Turtle();
}

function draw() {
	background(50);

	noFill();
	stroke(255);
// 	stroke(19, 37, 66);
	strokeWeight(1.2);

	myTurtle.penUp();
	myTurtle.moveTo(60, 300);
	myTurtle.penDown();

	// draw the bottom
	for (var i = 0; i < 22; i++) {
	    myTurtle.pushState();
	    myTurtle.turnRight(18 + i * 2.5);
	    for (var j = 0; j < 30; j++) {
	        myTurtle.turnLeft(1 + i / 6.3);
		    myTurtle.moveForward(11 + i / 5);
	    }
	    myTurtle.turnRight(i / 10);
	    for (var m = 0; m < 16; m++) {
	        myTurtle.turnRight(1 + i / 2);
		    myTurtle.moveForward(8);
	    }
	    myTurtle.popState();
	}

	// draw the top
// 	stroke(1, 14, 35);
	for (var i = 0; i < 18; i++) {
	    strokeWeight(2);
	    myTurtle.pushState();
	    myTurtle.turnLeft(15 + i);
	    for (var j = 0; j < 30; j++) {
	        myTurtle.turnRight(1 + i / 17);
		    myTurtle.moveForward(12.4);
	    }
	    strokeWeight(1);
	    myTurtle.turnLeft(i / 10);
	    for (var m = 0; m < 18; m++) {
	        myTurtle.turnLeft(1 + i / 5);
		    myTurtle.moveForward(7);
	    }
	    myTurtle.popState();
	}

	//draw water
// 	stroke(255);
	myTurtle.penUp();
	myTurtle.moveTo(130, 230);
	myTurtle.turnLeft(90);
	myTurtle.penDown();

	for (var i = 0; i < 10; i++) {
	    //water bottom
	    myTurtle.pushState();
	    myTurtle.turnLeft(i / 2);
	    myTurtle.moveForward(40);
	    for (var m = 0; m < 22; m++) {
	        myTurtle.turnLeft(m / 2.2);
		    myTurtle.moveForward(5);
	    }
	    myTurtle.popState();

	    myTurtle.pushState();
	    myTurtle.turnRight(i / 2);
	    myTurtle.moveForward(40);
	    for (var m = 0; m < 22; m++) {
	        myTurtle.turnRight(m / 2.2);
		    myTurtle.moveForward(5);
	    }
	    myTurtle.popState();
	}

