var myTurtle;

function setup() {
	createCanvas(500, 500);
	myTurtle = new Turtle();
}

function draw() {
	background(20);

	noFill();
	stroke(255,200);
// 	strokeWeight(3);

	myTurtle.penUp();
	myTurtle.moveTo(250, 250);
	myTurtle.penDown();

	drawHexagon(myTurtle, 6);

	noLoop();
}

function drawHexagon(t, n) {
    if (n > 0) {
        for (var i = 0; i < 6; i++) {
        strokeWeight(n * 3 + 2);
	    t.pushState();
		t.moveForward(n * 25 - 10);
		drawSquare(t);
        drawHexagon(t, n - 3);
		t.popState();
		t.turnRight(60);
        }
    }
}

function drawSquare(t) {
    strokeWeight(10);
    strokeCap(ROUND);
    t.turnLeft(90);
    t.moveForward(20);
    t.turnLeft(90);
    t.moveForward(50);
    t.turnLeft(90);
    t.moveForward(10);
    t.turnLeft(75);
    t.moveForward(20);
}

function keyPressed() {
  if (key === 'S') {
    save('canvas.jpg');
  }
}

