function setup() {
    createCanvas(windowWidth, windowHeight);
    noLoop();
    background(random(255),random(255),random(255));
}

function draw() {
    stroke(random(255),random(255),random(255));

    drawBrownianPath(width * 0.5, height * 0.5, 10, 1000);

    stroke(random(255),random(255),random(255));

    drawBrownianPath2(width * -0.5, height * -0.5, 1000, 10);
}

function drawBrownianPath(x, y, segmentLength, steps) {
    for (var step = 0; step < steps; step++) {

        var nextX, nextY;

        nextX = x + floor(random(-1,2)) * segmentLength;
        nextY = y + floor(random(-1,2)) * segmentLength;

        line(x+x, y+y, nextX, nextY);

        x = nextX;
        y = nextY;
    }
}

function drawBrownianPath2(x, y, segmentLength, steps) {
    for (var step = 0; step < steps; step++) {

        var nextX, nextY;

        nextX = y + floor(random(-1,2)) * segmentLength/2;
        nextY = x + floor(random(-1,2)) * segmentLength;

        line(x, y, nextX, nextY);

        x = nextX;
        y = nextY;
    }
}

