    var target_word = words[i];
    var next_word = words[i + 1];

    if (!ngrams[target_word]) {
      ngrams[target_word] = [];
    }

    ngrams[target_word].push(next_word);
  }

  button = createButton("generate");
  button.mousePressed(markovIt);
  frameRate(10);
}

function draw() {

  if (start) {
    background(0);
    var x = "    ";
    for (var i = 0; i < frameCount - startFrame; i++) {
      x += " " + splitResult[i];
    }

    if (splitResult.length == frameCount - startFrame) {
      start = false;
    }
    textSize(18);
   text(x, width/2 - 300, 100, 600);
  }
}


function markovIt() {
  result = first_word;
  var current_word = first_word;



  for (var i = 0; i < 300; i++) {

    var index = floor(random(ngrams[current_word].length));
    current_word = ngrams[current_word][index];

    result += " ";
    result += current_word;

    var last_character = current_word.substr(current_word.length - 1);
    if (last_character === ".") {
      break;
    }
  }

  splitResult = split(result, " ");

  start = true;
  startFrame = frameCount;

}

