function ball() {
 this.x = random(width);
 this.y = 0;
 this.diameter = random(10, 30);
 this.xspeed = 0;
 this.yspeed = 0;

 this.update = function() {
  this.xspeed = random(-5 - score, 5 + score);
  this.yspeed += gravity;

  if (abs(this.y + this.diameter - mouseY) < this.yspeed && abs(this.x - mouseX) < broadWidth / 2) {
   this.yspeed = -this.yspeed;
   backgroundColor = 250;
   score++;
  }

 }


 this.move = function() {

  if (this.x <= this.diameter || this.x >= width - this.diameter) {
   this.xspeed = -this.xspeed;
  }

  this.x += this.xspeed;
  this.y += this.yspeed;
 };

 this.display = function() {
  ellipse(this.x, this.y, this.diameter, this.diameter);
 };
}



function keyTyped(){

 if(key === "r"){
  alive = true;
  score = 0;
  ball1 = new ball();
 }
}

