var EXPORT = false;

function setup() {

    createCanvas(600, 600);
    frameRate(1);

}

function draw() {

    background(152,251,152);
    stroke(0);
    strokeWeight(1);
    strokeCap(ROUND);
    noFill();

    var currentdate = new Date(); 

	var hours = currentdate.getHours();
	var minutes = currentdate.getMinutes();
	var seconds = currentdate.getSeconds();

    ellipse(width / 2, height / 2, 550, 550);

    var diameter = 550;
    var radius = diameter / 2;
    var inner_radius = radius - 50;
    var shorter = radius - 100;
    var center = width / 2;

    // ### Seconds ###

    console.log(seconds);

	var s_angle = map(seconds, 0, 60, 0, TWO_PI) - HALF_PI;

	var xs = cos(s_angle) * inner_radius;
    var ys = sin(s_angle) * inner_radius;

	stroke(0);
	strokeWeight(1);
    line(xs + center, ys + center, center, center);

    // ### Minutes ###

    console.log("minute is " + minutes);

	var m_angle = map(minutes, 0, 60, 0, TWO_PI) - HALF_PI;

	var xm = cos(m_angle) * inner_radius;
    var ym = sin(m_angle) * inner_radius;

	stroke(0);
	strokeWeight(10);
    line(xm + center, ym + center, center, center);

    // ### Hours ###

    console.log("hour is " + hours);

	var h_angle = map(hours, 0, 12, 0, TWO_PI) - HALF_PI;

	var xh = cos(h_angle) * shorter;
    var yh = sin(h_angle) * shorter;

	stroke(0);
	strokeWeight(10);
    line(xh + center, yh + center, center, center);

    console.log(center);

    // ### Outer Stuff ##

    var points = 60;
	var pointAngle = 360 / points;

	strokeWeight(1);

	var longer = radius - 25;

	for(var angle = 0; angle < 360; angle = angle + pointAngle) { 

		var x = cos(radians(angle)) * longer; 
		var y = sin(radians(angle)) * longer;

		ellipse(x + center, y + center, 8, 8);

	}

	strokeWeight(10);

	var points2 = 12; 
	var pointAngle2 = 360 / points2;

	for(var angl2e = 0; angle2 < 360; angle2 = angle2 + pointAngle2) { 

		var x1 = cos(radians(angle2)) * longer; 
		var y1 = sin(radians(angle2)) * longer;

		var x2 = cos(radians(angle2)) * inner_radius; 
		var y2 = sin(radians(angle2)) * inner_radius;

		line(x1 + center, y1 + center, x2 + center, y2 + center);

	}








    if (EXPORT) {

	    saveFrame("EXPORT", frameCount, "jpg", 90);

	}

	// if (frameCount > 90) {

	//     noLoop();

	// }

}


function saveFrame(name, frameNumber, extension, maxFrame) {

    if (maxFrame && frameNumber > maxFrame) {

        return;

    }

    if (!extension) {

        extension = "png";

    }

    frameNumber = floor(frameNumber);
    var paddedNumber = ("0000" + frameNumber).substr(-4, 4);
    save(name + "_" + paddedNumber + "." + extension);

}

