	myTurtle = new Turtle();
}

function draw() {

	myTurtle.penUp();
	myTurtle.moveTo(250, 343);
	myTurtle.penDown();
	drawBranch(40);
	// }

	myTurtle.penUp();
	myTurtle.moveTo(350 + 100, 343);
	myTurtle.penDown();
	drawBranch(40);
	myTurtle.penUp();
	myTurtle.moveTo(350, 333);
	myTurtle.penDown();
	drawBranch(40);
	for (var i = 0; i < 20; i++) {
		myTurtle.penUp();
		myTurtle.moveTo(random(700), random(100, 350));
		myTurtle.penDown();
		drawCircle(myTurtle, 20);


	}
	myTurtle.penUp();
	myTurtle.moveTo(228, 350);
	myTurtle.penDown();
	myTurtle.turnRight(75);
	for (var i = 0; i < 31; i++) {
		stroke(252, 191, 191);
		strokeWeight(map(abs(i - 15), 0, 15, 3, 0.1));
		myTurtle.moveForward(8);
		myTurtle.turnRight(1);
	}


}

function drawCircle(t, length) {

	strokeWeight(25 - length);
	stroke(252, 191, 191, 10);
	for (var i = 0; i < 360; i++) {
		t.moveForward(random(0.07));
		t.turnRight(1);
	}
}

function drawBranch(length) {

	if (length < 13) {
		drawCircle(myTurtle, length);
		return;
	}
	strokeWeight(length / 10);
	// draw this branch
	stroke(25);
	myTurtle.moveForward(length);

	// left child
	myTurtle.pushState();
	myTurtle.turnLeft(random(10, 30));
	drawBranch(length * random(.7, .8));
	drawBranch(length * random(.7, .8));

	myTurtle.popState();

	//right child
	myTurtle.pushState();
	myTurtle.turnRight(random(10, 30));
	drawBranch(length * random(.7, .8));
	drawBranch(length * random(.7, .8));
	myTurtle.popState();

}

