var EXPORT = false;

        function setup() {
            pixelDensity(1)
            createCanvas(500, 400);
            frameRate(30);
            textAlign(CENTER);
            noiseDetail(1);
        }

        function draw() {
            blendMode(BLEND);
            background(0);

            blendMode(ADD);
            fill(0.1);
            var x = width * .3;
            var y = height * .55;

            for (i = 0; i < 200; i++) {
                var s = sin(frameCount / 90 * PI) * 100;

                var offsetX = map(noise(i * .5, frameCount * .01, 0), 0, .5, -400, 200);
                var offsetY = map(noise(i * .5, frameCount * .01, 1), 0, .5, -200, 200);
                fill(10);
                textSize(s)
                text("CompForm", x + offsetX, y + offsetY);
                textSize(s)
                text("0", x + offsetX + s, y + offsetY);
                textSize(s)
                text("1", x + offsetX - 10 * s, y + offsetY + 10 * s);
            }

            var c = sin(frameCount / 5 * PI) * 100;
            fill((frameCount/12)*(frameCount/12))
            textSize(6 + c)
            text("Comp Form", x+100, y);

            var s = sin(frameCount / 5 * PI) * 100;
            fill((frameCount/15)*(frameCount/15))
            textSize(8 + s)
            text("Comp Form", x+100, y);

            if (EXPORT) {
                saveFrame("EXPORT", frameCount, "jpg", 90);
            }
            if (frameCount > 90) {
                noLoop();
            }
        }

        // saveFrame - a utility function to save the current frame out with a nicely formatted name
        // format: name_####.extension
        // name: prefix for file name
        // frameNumber: number for the frame, will be zero padded
        // extension: jpg or png, controls file name and image format
        // maxFrame: checked against frameNumber, frames beyond maxFrame are not saved
        function saveFrame(name, frameNumber, extension, maxFrame) {
            // don't save frames once we reach the max
            if (maxFrame && frameNumber > maxFrame) {
                return;
            }

            if (!extension) {
                extension = "png";
            }
            // remove the decimal part (just in case)
            frameNumber = floor(frameNumber);
            // zero-pad the number (e.g. 13 -> 0013);
            var paddedNumber = ("0000" + frameNumber).substr(-4, 4);

            save(name + "_" + paddedNumber + "." + extension);
        }

