function setup() {
	createCanvas(windowWidth, windowHeight);
	noLoop();
}

function draw() {
	background(random(220), random(160), random(140));
        strokeWeight(random(2));
	stroke(220, 220, 220);
	drawBrownianPath(width * random(0.5), height * 0.5, 
        random(100), random(100));
	drawBrownianPath(width * random(0.5), height * 0.5, 
        random(20), random(100));
}

function drawBrownianPath(x, y, segmentLength, steps) {
	for (var step = 0; step < steps; step++) {

		if (random() < 0.5) {
			nextX = x - segmentLength;
		} else {
			nextX = x + segmentLength;
		}

		if (random() < 0.5) {
			nextY = y - segmentLength;
		} else {
			nextY = y + segmentLength;
		}
		line(x, y, nextX, nextY);
		line(y, x, nextX, nextY);
		x = nextX;
		y = nextY;
	}
}

