  drawHorizontal(typeface.f, 15);
  drawVertical(typeface.f, 25);

}

function drawHorizontal(letter, length) {
  for (let k = 0; k < letter.horizontal.length; k++) {
    for (let strokes = 0; strokes < density; strokes++) {
      myTurtle.moveTo(letter.horizontal[k][0] + random(-10, 10), letter.horizontal[k][1]);
      myTurtle.penDown();

      // Drawing path
      for (let i = 0; i < length; i++) {
        myTurtle.moveForward(10);
        myTurtle.turnTo(random(-stroke_variance, stroke_variance));
      }

      myTurtle.penUp();

    }
  }

}

function drawVertical(letter, length) {
  for (let strokes = 0; strokes < density; strokes++) {
    myTurtle.moveTo(letter.vertical[0][0] + random(-10, 10), letter.vertical[0][1]);
    myTurtle.penDown();
    myTurtle.turnTo(90);

    // Drawing path
    for (let i = 0; i < length; i++) {
      myTurtle.moveForward(10);
      myTurtle.turnTo(random(-stroke_variance + 90, stroke_variance + 90));
    }

    myTurtle.penUp();

  }
}

