color[] cols= new color[3]; 
IntList dice;
void setup() { 
  size(800, 800);
  background(250);
  //frameRate(60);
  i=0;
  j=0;
  r=40;
  dice = new IntList();
  dice.append(0);
  dice.append(1);
  dice.append(2);
  cols[0]=color(255, 184, 103);
  cols[1]=color(104, 92, 121);
  cols[2]=color(172, 108, 130);
} 
void draw() { 
  step();
}
void step() {
  noStroke();
  if(i%20==0 && j%20==0){
  dice.shuffle();
  }
  fill(cols[dice.get(0)]);
  posx = j%20*40;
  posy = i%20*40;
  rect(posx, posy, 40, 40);
  int run=int(random(2));
  stroke(cols[dice.get(1)]);
  strokeWeight(2);
  switch (run)
  {
  case 0:
    arc(posx, posy, r, r, 0, HALF_PI);
    arc(posx+40, posy+40, r, r, PI, PI+ HALF_PI);
    stroke(cols[dice.get(2)]);
    line(posx+40, posy, posx, posy+40);
    break; 
  case 1:
    arc(posx+40, posy, r, r, HALF_PI, PI );
    arc(posx, posy+40, r, r, PI+ HALF_PI, PI+PI);
    stroke(cols[dice.get(2)]);
    line(posx, posy, posx+40, posy+40);
    break;
  }
  j++;
  if (j%20==0) {
    i++;
  }
}
