var xSlider, ySlider;

function setup() {
	createCanvas(window.innerWidth, window.innerHeight * .75);

	createP('X position');
	xSlider = createSlider(-100, 200, 100);

	createP('Y position');
	ySlider = createSlider(0, 200, 100);

	noStroke();
	background(0);
}

function draw() {
	var x = xSlider.value();
	var y = ySlider.value();

	for (i = 0; i < 100; i++) {
		push();

		from = color(215, 200, 211, 10);
		to = color(100, 100, 100, 10);
		c1 = lerpColor(from, to, .5);
		fill(c1);
		translate(width * 0.5 + 10 * i, height * 0.5 + 10 * i);
		rotate(sin(frameCount));
		ellipse(i * x, i * y, 1, 100 * frameCount / 100);
		star(2 * i * x, 2 * i * y, 5, 100 * frameCount / 100, 400);
		pop();
	}
}

function star(x, y, radius1, radius2, npoints) {
	var angle = TWO_PI / npoints;
	var halfAngle = angle / 40.0;
	beginShape();
	for (var a = 0; a < TWO_PI; a += angle) {
		var sx = x + sin(a) * radius2;
		var sy = y + cos(a) * radius2;
		vertex(sx, sy);
		sx = i * x + sin(a + halfAngle) * radius1;
		sy = i * y + sin(a + halfAngle) * radius1;
		vertex(sx, sy);
	}
	endShape(CLOSE);
}

