function setblock(index, x, y) {
	switch (index) {
		case 0:
			arc(x, y, 50, 50, 0, HALF_PI);
			break;
		case 1:
			arc(x + 25, y, 50, 50, HALF_PI, PI);
			break;
		case 2:
			arc(x + 25, y + 25, 50, 50, PI, PI + HALF_PI);
			break;
		case 3:
			arc(x, y + 25, 50, 50, PI + HALF_PI, 0);
			break;
		case 4:
			rect(x, y, 25, 25);
			break;
		case 5:
			ellipse(x + 12.5, y + 12.5, 25, 25);
			break;
	}





}

function updateblock(thisblock, seq, time) {
	var swi = (seq * 10 + time) % 90;

	if (swi < 10) {
		thisblock.x += 2.5
	} else if (swi < 20) {
		thisblock.y += 2.5;
	} else if (swi < 30) {
		thisblock.x -= 2.5;
	} else if (swi < 40) {
		thisblock.y -= 2.5;
	}
	return thisblock;

}

function saveFrame(name, frameNumber, extension, maxFrame) {
	// don't save frames once we reach the max
	if (maxFrame && frameNumber > maxFrame) {
		return;
	}

	if (!extension) {
		extension = "png";
	}
	// remove the decimal part (just in case)
	frameNumber = floor(frameNumber);
	// zero-pad the number (e.g. 13 -> 0013);
	var paddedNumber = ("0000" + frameNumber).substr(-4, 4);

	save(name + "_" + paddedNumber + "." + extension);
}

