var midx,midy;

function setup() {
  createCanvas(800, 400);
}

function draw() {
  background(0);

  img = createImage(400,200);
  img.loadPixels();
  midx=img.width*0.5;
  midy=img.height*0.5;
  colorMode(HSB, 360, 100, 100,100);
  for (var y = 0; y < img.height; y++) {
    for (var x = img.width; x > -1; x--) {
      c = color(252, y*0.1+40, 15+x*0.05);
      img.set(x, y, c);
    }
  }
  for (i = 0; i < 50; i++) {
    r = 60;
    g = random(20, 70);
    b = random(40,50);
    c = color(r, g, b);
    var px = floor(random(img.width));
    var py = floor(random(img.height));
    img.set(px, py, c);
    img.set(px + 1, py, c);
    img.set(px - 1, py, c);
    img.set(px, py + 1, c);
    img.set(px, py - 1, c);
    c = color(r, g - 30, b - 20);
    img.set(px + 2, py, c);
    img.set(px - 2, py, c);
    img.set(px, py + 2, c);
    img.set(px, py - 2, c);

  }
  img.updatePixels();
  for (var y = 0; y < img.height; y++) {
    var dis = y - midy;
    var pox = floor(sqrt(pow(20, 2) - pow(dis, 2)));
    for (var x = img.width; x > -1; x--) {
      c = color(350 - y, 90, 50);
      for (i = 0; i < 1; i += 0.125) {
        var orbit = round(sqrt(pow(img.height * i, 2) - pow(dis, 2)));
        if (x == midx - orbit || x == orbit + midx) {
          c = color(252, 60-y*0.1, 55+x*0.05);
          img.set(x, y * 0.2 + img.height * 0.4, c);
        }
      }
      if (midx - pox <= x && x <= pox + midx) {
        c = color(random(40), 100, 100);
        img.set(x, y, c);
      }
    }
  }
  var size=[2,2,4,4,6,8,8,8];
 var dira =shuffle([1,1,1,1,-1,-1,-1,-1]);
  for (i = 0.125; i < 1; i += 0.125) {
    var ang=random(0.40,0.60)*PI;
    var dir =dira[i*8];
    setP(size[i*8-1],midx+dir*i*midx,midy+cos(ang)*midx*i*0.2);
  }
  img.updatePixels();

  noSmooth();
  image(img, 0, 0, width, height);
  noLoop();

}
function setP(w, _posx, _posy) {
  for (var y = _posy - w; y < _posy + w; y++) {
    var dis = y - _posy;
    var pox = floor(sqrt(pow(w, 2) - pow(dis, 2)));
    for (var x = _posx + pox; x > _posx - 1 - pox; x--) {
      var centdis=dist(x,y,midx,midy);
      c = color(random(centdis*2 - 40, centdis*2), random(50,100),  random(80,100));
      img.set(x, y, c);
    }
  }
}
