            myTurtle = new Turtle();
        }

        function draw() {
            strokeWeight(10);
            stroke(255);
            myTurtle.penUp();
            myTurtle.moveTo(250, 300);
            myTurtle.penDown();

            // top of snout
            for (var i = 0; i < 15; i++) {
                myTurtle.moveForward(5);
                myTurtle.turnLeft(5);
            }
            // head
            for (var i = 0; i < 15; i++) {
                myTurtle.moveForward(5);
                myTurtle.turnRight(5);
            }
            // ear
            myTurtle.turnLeft(135);
            for (var i = 0; i < 340; i++) {
                myTurtle.moveForward(0.5);
                myTurtle.turnRight(0.8);
            }
            // body
            myTurtle.turnRight(180);
            for (var i = 0; i < 120; i++) {
                myTurtle.moveForward(3);
                myTurtle.turnRight(0.8);
            }
            for (var i = 0; i < 40; i++) {
                myTurtle.moveForward(2);
                myTurtle.turnRight(1);
            }
            // push state to put tail
            myTurtle.pushState()
            for (var i = 0; i < 20; i++) {
                myTurtle.moveForward(3);
                myTurtle.turnRight(4);
            }
            for (var i = 0; i < 120; i++) {
                myTurtle.moveForward(3);
                myTurtle.turnRight(0.15);
            }
            for (var i = 0; i < 40; i++) {
                myTurtle.moveForward(3);
                myTurtle.turnRight(0.6);
            }
            // tail
            myTurtle.popState()
            myTurtle.turnLeft(90);
            for (var i = 10; i > 5; i-=0.25) {
                strokeWeight(i);
                myTurtle.moveForward(15);
                myTurtle.turnLeft(4);
            }
